INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75275, 'Fedratinib', 'Verapamil', 'Minor', 'Coadministration of fedratinib with moderate CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of fedratinib, which is a substrate of the isoenzyme. However, clinical data are not available. Until further information is available, caution and monitoring for adverse effects including anemia, thrombocytopenia, gastrointestinal toxicity, and hepatic and pancreatic enzyme elevations is advised if fedratinib is used with moderate CYP450 3A4 inhibitors.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75362/', '', 'Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Captopril, More', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
