INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75294, 'Fostamatinib', 'Verapamil', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75381/', '', 'Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Captopril, More', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Coagulation Factor IX Human', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75295, 'Fostemsavir', 'Verapamil', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of temsavir, the active moiety of fostemsavir. Increased temsavir exposure may increase the risk of QT prolongation.', 'DDInter', 'No dosage adjustments are recommended for fostemsavir when used with potent CYP450 3A4, P-gp, and/or BCRP inhibitors. It may be advisable to monitor patients for increased adverse effects such as QT prolongation during coadministration, particularly in the elderly and patients with risk factors for torsade de pointes.', 'Metabolism, Absorption', 'No dosage adjustments are recommended for fostemsavir when used with potent CYP450 3A4, P-gp, and/or BCRP inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75382/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Brincidofovir, Remdesivir, Elbasvir, Voxilaprevir, Maribavir, More', 1767369485);
