INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75356, 'Verapamil', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', 'DDInter', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75443/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75357, 'Verapamil', 'Linezolid', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75444/', '', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Telavancin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75358, 'Verapamil', 'Lisinopril', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75445/', '', 'Trandolapril, Lisinopril', 'Verapamil, Bempedoic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75359, 'Verapamil', 'Lithium carbonate', 'Moderate', 'Verapamil has variable effects on lithium levels. Increases, decreases, and no change in serum lithium levels have all been reported. The mechanism is unknown.', 'DDInter', 'Patients who must take both drugs should be monitored for evidence of lithium toxicity. Patients should be advised to notify their physician if they experience symptoms of possible lithium toxicity such as drowsiness, dizziness, confusion, ataxia, vomiting, diarrhea, thirst, blurry vision, tinnitus, or tremor.', 'Others', 'Patients who must take both drugs should be monitored for evidence of lithium toxicity.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75446/', '', '', '', 1767369485);
