INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75420, 'Verapamil', 'Nortriptyline', 'Moderate', 'Concurrent administration of verapamil may increase tricyclic antidepressant (TCA) serum concentrations. The mechanism appears to be inhibition of CYP450 metabolism. Pharmacologic and toxic effects of TCAs may be increased.', 'DDInter', 'Close monitoring for clinical response and tolerance is recommended whenever verapamil is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Dose adjustments may be necessary.', 'Metabolism', 'Close monitoring for clinical response and tolerance is recommended whenever verapamil is added to or discontinued from an antidepressant regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75507/', '', 'Desvenlafaxine, St. John''s Wort, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Esketamine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75421, 'Verapamil', 'Octreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75508/', '', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Ganirelix, Gonadorelin', 'Moexipril, Perindopril, Benazepril, Quinapril, Trandolapril, Captopril, Fosinopril, Indapamide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75422, 'Verapamil', 'Olanzapine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75509/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75423, 'Verapamil', 'Olaparib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme. Increased exposure to olaparib may increase the risk of adverse effects such as hematologic toxicity, nausea, vomiting, diarrhea, anorexia, dyspepsia, and abdominal pain or discomfort.', 'DDInter', 'Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible. If coadministration is required, dose reductions are recommended as follows: the olaparib tablet dosage should be reduced to 150 mg twice a day; the olaparib capsule dosage should be reduced to 200 mg twice a day. Specific dose recommendations for each formulation should be followed as the tablet and capsule formulations are not interchangeable due to differences in bioavailability. Once the CYP450 3A4 inhibitor has been discontinued for 3 to 5 elimination half-lives, the usual olaparib dose should be resumed.', 'Metabolism', 'Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75510/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Mitotane, Porfimer sodium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75424, 'Verapamil', 'Oliceridine', 'Major', 'Coadministration with moderate or potent inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations and adverse opioid effects of oliceridine, which is primarily metabolized by both isoenzymes in vitro. In addition, oliceridine concentrations may decrease following discontinuation of a concomitant moderate or potent CYP450 2D6 or 3A4 inhibitor, possibly resulting in decreased opioid efficacy or withdrawal syndrome. The effect of a CYP450 2D6 inhibitor on the pharmacokinetics of oliceridine has not been studied. However, it may be similar to that reported in CYP450 2D6 poor metabolizers, whose plasma clearance of oliceridine is reduced by approximately 50% compared to those who are nonpoor CYP450 2D6 metabolizers.', 'DDInter', 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4. If concomitant use is considered necessary, patients may require less frequent dosing of oliceridine and should be closely monitored for respiratory depression, sedation, and QT prolongation. If a moderate or potent CYP450 2D6 or 3A4 inhibitor is discontinued, an increase of the oliceridine dosage may be considered while monitoring for signs of opioid withdrawal until stable drug effects are achieved.', 'Metabolism', 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75511/', '', 'Naltrexone, Tapentadol, Oxymorphone, Dezocine, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75425, 'Verapamil', 'Opium', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75512/', '', 'Eluxadoline, Diphenoxylate, Difenoxin, Naltrexone, Tapentadol, Oxymorphone, Dezocine, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75426, 'Verapamil', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range.', 'Metabolism', 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75513/', '', 'Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Captopril, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75427, 'Verapamil', 'Osimertinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases), and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75514/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75428, 'Verapamil', 'Oxaprozin', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75515/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Phenylbutazone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75429, 'Verapamil', 'Oxcarbazepine', 'Moderate', 'Coadministration with oxcarbazepine may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by oxcarbazepine.', 'DDInter', 'Caution is advised if oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oxcarbazepine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75516/', '', 'Moexipril, Perindopril, Bisoprolol, Benazepril, Trandolapril, Captopril, Fosinopril', 'Brivaracetam, Rufinamide, Methsuximide, Ezogabine, Cenobamate, Paramethadione, Perampanel, Fenfluramine, Phensuximide, Phenacemide, Felbamate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75430, 'Verapamil', 'Oxtriphylline', 'Moderate', 'Verapamil may increase theophylline serum levels, theophylline effect, and risk of toxicity. The mechanism is inhibition of CYP450 hepatic metabolism by verapamil. Other hepatically metabolized methylxanthines may also be affected. Cigarette smokers appear to have a greater risk of developing theophylline toxicity from this combination.', 'DDInter', 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians. Theophylline dosage should be reduced as appropriate. Isradipine and nifedipine appear to have minimal effects on theophylline pharmacokinetics.', 'Metabolism', 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75517/', '', 'Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Benazepril, Quinapril, Trandolapril, Captopril, More', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Bromotheophylline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75431, 'Verapamil', 'Oxycodone', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of oxycodone, which is substantially metabolized by the isoenzyme. Increased oxycodone concentrations could conceivably increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Because oxycodone is also partially metabolized by CYP450 2D6, the magnitude of interaction may be even greater with concomitant use of a CYP450 3A4 and a CYP450 2D6 inhibitor, or concomitant use of a drug that is a dual inhibitor of both isoenzymes.', 'DDInter', 'Extreme caution is advised if oxycodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.g., azole antifungal agents, protease inhibitors, aprepitant, ciprofloxacin, chloramphenicol, clarithromycin, cobicistat, conivaptan, crizotinib, delavirdine, diltiazem, dronedarone, erythromycin, fusidic acid, idelalisib, imatinib, mibefradil, mifepristone, nefazodone, netupitant, quinupristin-dalfopristin, telithromycin, verapamil) or weak inhibitors that also inhibit CYP450 2D6 (e.g., abiraterone, amiodarone, cimetidine, pazopanib, ranolazine).', 'Metabolism', 'Extreme caution is advised if oxycodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75518/', '', 'Naltrexone, Tapentadol, Oxymorphone, Dezocine, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75432, 'Verapamil', 'Ozanimod', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', 'DDInter', 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75519/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75433, 'Verapamil', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of paclitaxel, which has been found in vitro to be a substrate of these isoenzymes.', 'DDInter', 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75520/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75434, 'Verapamil', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme.', 'DDInter', 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors. Dosage adjustment for palbociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors. However, patients should be monitored for increased adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis.', 'Metabolism', 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75521/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
