INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75454, 'Verapamil', 'Pipecuronium', 'Moderate', 'Some calcium channel blockers may enhance the neuromuscular blocking properties of nondepolarizing muscle relaxants. The mechanism is not known, but may involve blockage of calcium channels on skeletal muscle. Data are available for verapamil, diltiazem, nicardipine, atracurium, vecuronium, and pancuronium.', 'DDInter', 'Patients should be closely monitored for prolonged neuromuscular blockade. Reduced doses of muscle relaxants may be necessary. Profound neuromuscular blockade may be reversed by neostigmine or edrophonium.', 'Synergism', 'Patients should be closely monitored for prolonged neuromuscular blockade.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75541/', '', 'Enalapril, Moexipril, Ramipril, Lisinopril, Perindopril, Benazepril, Quinapril, Trandolapril, Captopril, Fosinopril', 'Botulinum toxin type A', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75455, 'Verapamil', 'Piroxicam', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75542/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Phenylbutazone, Tolazoline, Dimethyl sulfoxide, Phenylbutazone, Fluocinolone acetonide, Fluorometholone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75456, 'Verapamil', 'Pitolisant', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', 'DDInter', 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75543/', '', 'Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, More', 'Valbenazine, Riluzole, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
