INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75499, 'Verapamil', 'Rivaroxaban', 'Moderate', 'Coadministration with weak or moderate dual inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75586/', '', 'Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, More', 'Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Streptokinase, Tinzaparin, Vorapaxar, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75500, 'Verapamil', 'Rivastigmine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75587/', '', 'Moexipril, Perindopril, Benazepril, Trandolapril, Fosinopril', 'Ginkgo biloba, Aducanumab', 1767369485);
