INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75524, 'Verapamil', 'Nitrous acid', 'Moderate', 'Coadministration with antihypertensive agents, diuretics, vasodilators, or phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of sodium nitrite. Since sodium nitrite can cause serious hypotension and methemoglobin formation, patients may be at increased risk for complications related to inadequate perfusion and oxygenation.', 'DDInter', 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors. Hemodynamics should be monitored during and after administration of sodium nitrite, and the infusion rate decreased if significant hypotension occurs. In addition, methemoglobin levels should be monitored and oxygen administered during treatment whenever possible.', 'Synergism', 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75611/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, Methionine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75525, 'Verapamil', 'Sodium oxybate', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75612/', '', 'Esketamine, Ketamine, Nitrous oxide, Valbenazine, Riluzole, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', '', 1767369485);
