INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75578, 'Verapamil', 'Troglitazone', 'Moderate', 'Coadministration with troglitazone may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by troglitazone.', 'DDInter', 'Caution is advised if troglitazone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever troglitazone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if troglitazone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75665/', '', 'Exenatide, Lixisenatide, Albiglutide, Acetohexamide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Chlorpropamide, Semaglutide, More', 'Enalapril, Moexipril, Ramipril, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Captopril, Fosinopril', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75579, 'Verapamil', 'Troleandomycin', 'Moderate', 'Coadministration with certain macrolide antibiotics may increase the plasma concentrations and pharmacologic effects of verapamil. The proposed mechanism is macrolide inhibition of CYP450 3A4, the isoenzyme responsible for hepatic first-pass and systemic metabolic clearance of verapamil. Macrolides that may significantly inhibit CYP450 3A4 include clarithromycin, erythromycin, and troleandomycin. Azithromycin and dirithromycin are generally believed to have little, if any, effect on CYP450 3A4. Clinically, the interaction is supported by isolated case reports of elderly patients stabilized on verapamil and other medications who developed symptoms of verapamil toxicity (e.g., hypotension, bradycardia, atrioventricular block) within 2 to 7 days after initiation of erythromycin or clarithromycin therapy. One of the patients also had QT interval prolongation, which may be induced by elevated levels of erythromycin secondary to inhibition of intestinal P-glycoprotein and CYP450 3A4 by verapamil. The interaction was also suspected in a 53-year-old woman who was started on verapamil and clarithromycin concurrently and developed bradycardia, dizziness, hypotension and fainting after 24 hours. In all cases, discontinuation or dosage reduction of verapamil in addition to supportive care led to resolution of symptoms.', 'DDInter', 'Caution is advised if verapamil must be used with clarithromycin, erythromycin or troleandomycin, particularly in susceptible patients such as the elderly or debilitated. Cardiac function and pharmacologic response to verapamil should be monitored more closely whenever the macrolide antibiotic is added to or withdrawn from therapy in patients stabilized on their verapamil regimen, and the dosage adjusted as necessary. Azithromycin and dirithromycin may be safer alternatives during therapy with verapamil.', 'Metabolism', 'Caution is advised if verapamil must be used with clarithromycin, erythromycin or troleandomycin, particularly in susceptible patients such as the elderly or debilitated.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75666/', '', 'Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Captopril, More', 'Lincomycin, Dirithromycin', 1767369485);
