INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75769, 'Isradipine', 'Crizotinib', 'Moderate', 'Crizotinib can cause bradycardia and may have additive effects with other drugs that also slow the heart rate.', 'DDInter', 'Concomitant use of crizotinib with other agents known to cause bradycardia such as beta-blockers, calcium channel blockers, cholinesterase inhibitors, clonidine, and digoxin should generally be avoided whenever possible. Otherwise, heart rate and blood pressure should be monitored closely. If Grade 2 or 3 symptomatic bradycardia occurs, crizotinib should be withheld, the use of concomitant medications re-evaluated, and the dosage of crizotinib adjusted accordingly. Crizotinib should be permanently discontinued for Grade 4 bradycardia. In patients who develop Grade 4 bradycardia associated with concomitant medications known to cause bradycardia or hypotension, crizotinib should be withheld until Grade 1 or less, and if concomitant medications can be discontinued, crizotinib may be restarted at 250 mg once daily with frequent monitoring.', 'Synergism', 'Concomitant use of crizotinib with other agents known to cause bradycardia such as beta-blockers, calcium channel blockers, cholinesterase inhibitors, clonidine, and digoxin should generally be avoided whenever possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75856/', '', 'Celecoxib', 'Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75770, 'Istradefylline', 'Crizotinib', 'Moderate', 'Coadministration with crizotinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by crizotinib. The interaction has been studied with midazolam, a probe substrate for CYP450 3A4.', 'DDInter', 'Caution is advised when crizotinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever crizotinib is added to or withdrawn from therapy. Concomitant use of crizotinib and substrates of CYP450 3A4 with a narrow therapeutic index (e.g., antiarrhythmic agents, benzodiazepines, ergot derivatives, immunosuppressants, vinca alkaloids) should be avoided if possible.', 'Metabolism', 'Caution is advised when crizotinib is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75857/', '', 'Entrectinib, Acalabrutinib, Dacomitinib, Avapritinib, Vemurafenib, Selumetinib, Vandetanib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, More', '', 1767369485);
