INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75960, 'Crizotinib', 'Selegiline', 'Moderate', 'Coadministration with crizotinib may theoretically increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2B6 (e.g., bupropion), P-glycoprotein (P-gp) (e.g., dabigatran), and/or the organic cation transporters 1 and 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism, based on in vitro data, is decreased clearance due to crizotinib-mediated inhibition of CYP450 2B6, P-gp, and/or OCT1 or OCT2.', 'DDInter', 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever crizotinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Although the clinical significance is unknown, caution is advised if crizotinib is used concomitantly with drugs that are substrates of CYP450 2B6, P-gp, and/or OCT1 or OCT2, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76047/', '', 'Ropinirole, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Tolcapone, Rotigotine, Pergolide, Pramipexole, Safinamide', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 1767369485);
