INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75992, 'Crizotinib', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'DDInter', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76079/', '', 'Futibatinib, Copanlisib, Alpelisib, Lenvatinib, Erdafitinib, Neratinib, Abemaciclib, Vandetanib, Trametinib, Afatinib, Alectinib, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Enasidenib, Niraparib, Aflibercept, Trastuzumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75993, 'Crizotinib', 'Telaprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of crizotinib, which is primarily metabolized by the isoenzyme. Because crizotinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Concomitant use of crizotinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of crizotinib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of crizotinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76080/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Tucatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
