INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76323, 'Cyclobenzaprine', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to lorlatinib-mediated induction of CYP450 3A4 metabolism.', 'DDInter', 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy. Avoid concomitant use with lorlatinib where minimal decreases in concentration of the CYP450 3A4 substrate may lead to serious therapeutic failure. If coadministration is required, the CYP450 3A4 substrate dosage should be increased in accordance with approved product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76424/', '', 'Carisoprodol, Orphenadrine, Chlorzoxazone, Tizanidine, Baclofen, Methocarbamol, Chlormezanone', 'Entrectinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76324, 'Cyclobenzaprine', 'Loxapine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76425/', '', 'Amisulpride, Pimavanserin', '', 1767369485);
