INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76500, 'Belladonna', 'Phenylephrine (ophthalmic)', 'Moderate', 'The pressor response to phenylephrine may be potentiated by the vagolytic effect of atropine, which inhibits the reflex bradycardia that would normally accompany any increases in blood pressure induced by phenylephrine. Other antimuscarinic agents may also participate in this interaction, although clinical data are lacking.', 'DDInter', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents. Cardiovascular status, including blood pressure and heart rate, should be closely monitored. When using ophthalmic formulations, measures to minimize systemic absorption should be employed, such as digital compression of the lacrimal sac or lid closure after instillation. A milder strength (< 10%) is preferable if phenylephrine ophthalmic solution is given.', 'Synergism', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76601/', '', '', '', 1767369485);
