INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76640, 'Isavuconazonium', 'Cyclophosphamide', 'Moderate', 'Coadministration of isavuconazonium sulfate (prodrug of isavuconazole) with drugs that are substrates of CYP450 3A4 and/or 2B6, such as cyclophosphamide, may have variable effects on the plasma concentrations of these drugs. Isavuconazole is a moderate inhibitor of CYP450 3A4 as well as a mild inducer of CYP450 2B6, and the resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 'Until more information is available, caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of CYP450 3A4 and/or 2B6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Until more information is available, caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of CYP450 3A4 and/or 2B6, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76741/', '', 'Temozolomide, Thiotepa, Carmustine, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, Melphalan, More', '', 1767369485);
