INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76705, 'Cyclophosphamide', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 3A4.', 'DDInter', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76806/', '', 'Temozolomide, Thiotepa, Carmustine, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Melphalan, Ifosfamide, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, Enasidenib, Belzutifan, More', 1767369485);
