INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76839, 'Enzalutamide', 'Cyclosporine', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76940/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Darolutamide, Anastrozole, Letrozole, Fulvestrant, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone, Exemestane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76840, 'Eplerenone', 'Cyclosporine', 'Moderate', 'Cyclosporine may cause hyperkalemia. The concurrent use of potassium-sparing diuretics and cyclosporine may result in or exacerbate hyperkalemia.', 'DDInter', 'The concomitant use of potassium-sparing diuretics and cyclosporine should be avoided. A different class of antihypertensive should be considered.', 'Synergism', 'The concomitant use of potassium-sparing diuretics and cyclosporine should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76941/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76841, 'Erythropoietin', 'Cyclosporine', 'Moderate', 'Concomitant use of cyclosporine with erythropoiesis-stimulating agents (ESAs) may produce additive increases on blood pressure. Either treatment alone may commonly cause hypertension, which can be severe or difficult to control in some cases. Additionally, use of ESAs may alter cyclosporine blood levels, since cyclosporine is bound by red blood cells.', 'DDInter', 'Caution is advised if cyclosporine is prescribed in combination with ESAs. Blood pressure should be adequately controlled before initiating treatment and monitored periodically during treatment. Patients who develop hypertension that is difficult to control by medications or dietary measures should have their ESA treatment reduced or withheld. In addition, cyclosporine blood levels should be checked regularly, as changes may occur following the addition, withdrawal, or change of dosage of ESA therapy. Dosage adjustments for cyclosporine may be required with increases in hematocrit levels.', 'Synergism', 'Caution is advised if cyclosporine is prescribed in combination with ESAs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76942/', '', 'Luspatercept, Daprodustat', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76842, 'Eprosartan', 'Cyclosporine', 'Moderate', 'Coadministration of cyclosporine with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia or precipitate acute renal failure. Cyclosporine causes afferent arteriolar vasoconstriction and decreased glomerular filtration. These effects may be significant when combined with ACE inhibitors or angiotensin II receptor antagonists, which block the mechanism that maintains glomerular filtration rate by constricting the efferent arterioles.', 'DDInter', 'Serum potassium levels and renal function should be checked regularly when cyclosporine is used in combination with ACE inhibitors or angiotensin II receptor antagonists. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Excretion', 'Serum potassium levels and renal function should be checked regularly when cyclosporine is used in combination with ACE inhibitors or angiotensin II receptor antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76943/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, More', 'Nebivolol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76843, 'Erdafitinib', 'Cyclosporine', 'Moderate', 'Coadministration with erdafitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter, resulting in increased toxicity of these drugs. In vitro, erdafitinib has been shown to be an inhibitor of P-gp.', 'DDInter', 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided. If concomitant use is required, administer erdafitinib at least 6 hours before or after administration of P-gp substrates with narrow therapeutic ranges.', 'Distribution', 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/76944/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Sunitinib, Regorafenib, Midostaurin, Copanlisib, Lenvatinib, Erlotinib, Abemaciclib, Trametinib, Vandetanib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
