INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (76981, 'Cyclosporine', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', 'DDInter', 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77082/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Rifamycin, Paromomycin, Micafungin, Oteseconazole, Selenium Sulfide, Thiabendazole, Econazole, Tioconazole, Chlorphenesin, Ascorbic acid, Methazolamide, More', 1767369485);
