INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77063, 'Cyclosporine', 'Pravastatin', 'Major', 'Coadministration with cyclosporine may increase the oral bioavailability of pravastatin. The proposed mechanism is cyclosporine inhibition of the intestinal efflux of pravastatin via MRP2 transporter.', 'DDInter', 'The benefits of the use of pravastatin in combination with cyclosporine should be carefully weighed against the potential risks. Pravastatin dosage should start at 10 mg/day and generally not exceed 20 mg/day when used in combination with cyclosporine. Higher dosages should be used with caution. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Absorption', 'The benefits of the use of pravastatin in combination with cyclosporine should be carefully weighed against the potential risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77164/', '', 'Ustekinumab, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Canakinumab, Secukinumab, Ravulizumab, Diroximel fumarate, Abatacept, Upadacitinib, Siponimod, Anakinra, More', 'Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Colestipol, Bempedoic acid, Alirocumab, Bempedoic acid', 1767369485);
