INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77078, 'Cyclosporine', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally. Relugolix is a substrate for intestinal P-gp.', 'DDInter', 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours. Patients should be monitored more frequently for adverse effects. Alternatively, treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.', 'Absorption', 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77179/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Ganirelix, Cetrorelix, Nafarelin, Gonadorelin, Darolutamide, Anastrozole, Letrozole, Fulvestrant, Abarelix, Flutamide, Bicalutamide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77079, 'Cyclosporine', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77180/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Upadacitinib, Anakinra, More', 'Cabotegravir, Zanamivir, Velpatasvir, Stavudine, Elbasvir, Tecovirimat, Pibrentasvir, Rimantadine, Elvitegravir, Peramivir, Emtricitabine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77080, 'Cyclosporine', 'Repaglinide', 'Major', 'Coadministration with cyclosporine may significantly increase the plasma concentrations of repaglinide. The proposed mechanism is cyclosporine inhibition of the hepatic uptake of repaglinide via organic anion transporting polypeptide (OATP) 1B1 and also its metabolism via CYP450 3A4.', 'DDInter', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended if repaglinide is coadministered with cyclosporine. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations. The manufacturers recommend limiting the total daily dose of repaglinide to 6 mg when coadministered with cyclosporine.', 'Metabolism', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended if repaglinide is coadministered with cyclosporine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77181/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Lixisenatide, Albiglutide, Guar gum, Acarbose, Pramlintide, Alogliptin, Liraglutide, Ertugliflozin, Semaglutide, Linagliptin, Nateglinide, More', 1767369485);
