INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77088, 'Cyclosporine', 'Rifaximin', 'Moderate', 'Coadministration with cyclosporine may increase the plasma concentrations of orally administered drugs that are substrates of the P-glycoprotein (P-gp) transporter such as rifaximin. The proposed mechanism is increased absorption of rifaximin in the intestine due to inhibition of P-gp mediated drug efflux by cyclosporine. Cyclosporine is also an inhibitor of organic anion transporting polypeptide (OATP), breast cancer resistance protein (BCRP) and a weak inhibitor of CYP450 3A4. The inhibition of each of these transporters may also have a part in the increase in rifaximin exposure by cyclosporine.', 'DDInter', 'Caution is advised if cyclosporine must be used concurrently with rifaximin. Monitoring for increased rifaximin adverse effects such as headache, nausea and peripheral edema is recommended.', 'Absorption', 'Caution is advised if cyclosporine must be used concurrently with rifaximin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77189/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Rifamycin, Paromomycin, Mupirocin, Rifamycin, Oxytetracycline, Demeclocycline', 1767369485);
