INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77217, 'Cyclosporine', 'Vincristine', 'Moderate', 'Limited data suggest that cyclosporine given to reverse multidrug resistance can enhance the toxicity of some antineoplastic agents such as dactinomycin and vincristine. The proposed mechanism is cyclosporine inhibition of multidrug resistance protein 1 (MDR1), also known as P-glycoprotein, a plasma membrane efflux transporter that has been demonstrated as a primary cause of multidrug resistance in tumors. Inhibiting P-glycoprotein increases the intracellular concentration of substrate antineoplastic agents, thereby potentiating their cytotoxic effects.', 'DDInter', 'Patients receiving antineoplastic agents that are known substrates of P-glycoprotein should be closely monitored for potentially increased toxicities during coadministration with cyclosporine, and their dosages adjusted as necessary.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77318/', '', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Topotecan, Teniposide, Vinorelbine', 1767369485);
