INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77491, 'Dabrafenib', 'Cerivastatin', 'Moderate', 'Coadministration with dabrafenib may alter the pharmacokinetics of HMG-CoA reductase inhibitors. Dabrafenib has been found in vitro to be an inhibitor of organic anion transporting polypeptide (OATP) 1B1 and 1B3. Since many statins including atorvastatin, fluvastatin, pitavastatin, pravastatin, rosuvastatin, and simvastatin acid (active metabolite of simvastatin) are thought to be substrates of the hepatic uptake transporters, increased plasma concentrations may be expected during coadministration with dabrafenib, although this has not been studied. On the other hand, dabrafenib has also been reported to be an inducer of CYP450 3A4 and 2C9 in vivo. Reduced plasma concentrations may occur for substrates of these isoenzymes such as atorvastatin, fluvastatin, lovastatin, and simvastatin.', 'DDInter', 'Until more information is available, caution is advised when dabrafenib is prescribed with HMG-Coa reductase inhibitors. Clinical and laboratory monitoring are recommended whenever dabrafenib is added to or withdrawn from therapy, and the statin dosage adjusted as necessary. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Until more information is available, caution is advised when dabrafenib is prescribed with HMG-Coa reductase inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77592/', '', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77492, 'Dabrafenib', 'Cisapride', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77593/', '', 'Metoclopramide', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 1767369485);
