INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77566, 'Indomethacin', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', 'DDInter', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77667/', '', 'Fluocinolone acetonide, Fluorometholone, Fenoprofen, Rabeprazole, Meclofenamic acid, Ketorolac, Phenylbutazone, Misoprostol, Ketoprofen, Glucosamine, Oxaprozin, More', 'Entrectinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, Ribociclib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (77567, 'Insulin human', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9, including many antidiabetic agents such as nateglinide, repaglinide, linagliptin, saxagliptin, and sulfonylureas.', 'DDInter', 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of dabrafenib, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination. Likewise, patients should be observed for hypoglycemia when dabrafenib is withdrawn from their therapeutic regimen.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77668/', '', 'Lixisenatide', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 1767369485);
