INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (78317, 'Danazol', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of larotrectinib.', 'DDInter', 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors. Patients should be monitored for adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paraesthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases), and the larotrectinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78418/', '', 'Ospemifene, Prasterone, Raloxifene, Bazedoxifene', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, More', 1767369485);
