INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (78401, 'Danazol', 'Teriflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78502/', '', 'Ulipristal, Mifepristone, Ospemifene, Raloxifene, Bazedoxifene', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (78402, 'Danazol', 'Tioguanine', 'Moderate', 'The concomitant or sequential use of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with thioguanine. A high risk of liver toxicity characterized by vascular endothelial damage has been reported with long-term continuous use of thioguanine, particularly in children receiving the drug as part of maintenance therapy for acute lymphoblastic leukemia and in other conditions associated with continuous use.', 'DDInter', 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Baseline and regular monitoring of hepatic function is recommended. Thioguanine therapy should be discontinued if there is evidence of toxic hepatitis or biliary stasis, as reversal of signs and symptoms of liver toxicity have been reported upon withdrawal. Early indications of liver toxicity are signs associated with portal hypertension such as thrombocytopenia out of proportion with neutropenia and splenomegaly. Elevations of liver enzymes have also been reported, but do not always occur.', 'Synergism', 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78503/', '', 'Fluorouracil, Pemetrexed, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, More', 'Ulipristal, Mifepristone, Ospemifene, Raloxifene, Bazedoxifene', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (78403, 'Danazol', 'Thyroid, porcine', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', 'DDInter', 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78504/', '', 'Ulipristal, Mifepristone, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', '', 1767369485);
