INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (78993, 'Saxagliptin', 'Mifepristone', 'Moderate', 'Based on in vitro inhibition data, coadministration with mifepristone may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by mifepristone.', 'DDInter', 'Caution is advised if mifepristone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever mifepristone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if mifepristone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79094/', '', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Ertugliflozin, Semaglutide, More', 1767369485);
