INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79661, 'Echinacea', 'Darunavir', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79762/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79662, 'Efavirenz', 'Darunavir', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of darunavir, even in the presence of cobicistat or low-dose ritonavir as a pharmacokinetic booster. Additionally, the plasma concentration of cobicistat may be reduced. Reduced darunavir plasma levels may lead to diminished virologic response and possible resistance to darunavir. The mechanism is efavirenz induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of darunavir and cobicistat.', 'DDInter', 'Caution is advised if darunavir-ritonavir is coadministered with efavirenz. Antiretroviral response should be monitored closely and darunavir-ritonavir dose adjusted if necessary. Some authorities recommend a dose adjustment of darunavir 600 mg-ritonavir 100 mg twice daily. Concomitant use of darunavir-cobicistat with efavirenz is not recommended.', 'Metabolism', 'Caution is advised if darunavir-ritonavir is coadministered with efavirenz.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79763/', '', 'Lamivudine, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Stavudine, Famciclovir, More', 'Paritaprevir, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Elbasvir, Voxilaprevir, Pibrentasvir, Rimantadine, Telbivudine, Efavirenz, More', 1767369485);
