INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79749, 'Darunavir', 'Ketoconazole', 'Moderate', 'Coadministration of ketoconazole or itraconazole with ritonavir- or cobicistat-boosted darunavir may result in increased plasma concentrations of both the azole antifungal agent and darunavir. The proposed mechanism is inhibition of CYP450 3A4, as all of these medicines are known substrates and inhibitors of the CYP450 3A4 isoenzyme.', 'DDInter', 'During coadministration with ritonavir- or cobicistat-boosted darunavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity. Some authorities recommend that the dosage of itraconazole should also be limited to 200 mg per day when prescribed with ritonavir- or cobicistat-boosted darunavir.', 'Metabolism', 'During coadministration with ritonavir- or cobicistat-boosted darunavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79850/', '', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, Ciclopirox, More', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, More', 1767369485);
