INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79850, 'Darunavir', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79951/', '', 'Paritaprevir, Glecaprevir, Simeprevir, Amprenavir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, Dolutegravir, More', 'Lamivudine, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Stavudine, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79851, 'Darunavir', 'Repaglinide', 'Moderate', 'Coadministration with protease inhibitors may alter the hypoglycemic effect of repaglinide. Pharmacodynamically, protease inhibitors may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes. However, since they are potent inhibitors of CYP450 3A4, they may also increase the plasma concentrations and pharmacologic effects of repaglinide, which is partially metabolized by the isoenzyme.', 'DDInter', 'Pharmacologic response to repaglinide should be monitored more closely whenever a protease inhibitor is added to or withdrawn from therapy. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations. The repaglinide dosage may require adjustment if an interaction is suspected. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', 'Pharmacologic response to repaglinide should be monitored more closely whenever a protease inhibitor is added to or withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79952/', '', 'Guar gum', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Remdesivir, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79852, 'Darunavir', 'Retapamulin (topical)', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme. However, dosage adjustments are not necessary due to the low systemic exposure to retapamulin following topical application.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79953/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79853, 'Darunavir', 'Revefenacin', 'Moderate', 'Coadministration of revefenacin with inhibitors of organic anion transporting polypeptide (OATP) 1B1 and/or 1B3 may increase systemic exposure to its active metabolite, which has been reported to be a substrate of the hepatic uptake transporters.', 'DDInter', 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79954/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Rilpivirine, Etravirine, Dolutegravir, Oseltamivir, More', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Ipratropium, Cromoglicic acid, Nedocromil, Tiotropium, Beclomethasone dipropionate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79854, 'Darunavir', 'Rifabutin', 'Moderate', 'Coadministration of rifabutin and darunavir/ritonavir is expected to increase the plasma concentrations of rifabutin and decrease the plasma concentrations of darunavir. The proposed mechanism involves ritonavir inhibition of rifabutin metabolism via CYP450 3A4 and, conversely, rifabutin induction of darunavir metabolism via the same isoenzyme.', 'DDInter', 'To minimize the risk of rifabutin toxicity including leukopenia, uveitis, arthralgias and skin discoloration, darunavir labeling recommends that rifabutin dosage be reduced to 150 mg every other day in patients treated with darunavir/ritonavir. A complete blood count should be performed at least weekly and as clinically indicated to monitor for development of neutropenia.', 'Metabolism', 'To minimize the risk of rifabutin toxicity including leukopenia, uveitis, arthralgias and skin discoloration, darunavir labeling recommends that rifabutin dosage be reduced to 150 mg every other day in patients treated with darunavir/ritonavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79955/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, More', 'Lamivudine, Paritaprevir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79855, 'Darunavir', 'Rifampicin', 'Major', 'Coadministration with rifampin may significantly decrease the plasma concentrations of most protease inhibitors (PIs) except ritonavir. The mechanism is rifampin induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of PIs.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative antimycobacterial agents should be considered in patients already receiving effective PI-containing antiretroviral therapy. For treatment of latent tuberculosis (TB) infection, a nine-month regimen of isoniazid may be considered if feasible. For treatment of HIV-related TB, a regimen that includes rifabutin is generally preferred, as rifabutin appears to be as effective as rifampin but is a much less potent inducer of CYP450 3A4. Nonrifamycin-containing regimens may be suboptimal (higher mortality rates; higher rates of treatment failure and relapse; increased adverse effects; longer treatment duration) and are usually not recommended for HIV-related TB except in patients who are intolerant of rifamycins or infected with a rifamycin-resistant isolate. Alternatively, rifampin may be used at usual dosages in patients receiving an antiretroviral regimen that includes ritonavir 600 mg or 400 mg twice a day in combination with another PI at a reduced dosage. In patients who have not begun antiretroviral therapy at the time TB treatment is initiated, clinicians may also consider using rifampin and postponing antiretroviral therapy. With early HIV disease, it may be reasonable to monitor CD4 cell count and postpone antiretroviral therapy until TB treatment is complete, since there is low risk of HIV disease progression or death during this period. However, the optimal time for starting antiretroviral therapy should be individualized based on initial response to TB treatment and occurrence of side effects. In patients with low CD4 cell counts, clinicians may consider delaying antiretroviral therapy until after the first one or two months of TB therapy, as side effects are common during this multi-drug phase of TB treatment and may overlap with those of antiretroviral medications. Moreover, delaying antiretroviral therapy may ameliorate adherence issues and decrease the frequency and severity of paradoxical reactions (i.e., immune restoration syndromes resembling exacerbation of TB that sometimes occur after initiation of antituberculosis treatment in patients receiving potent antiretroviral therapy). Rifabutin can be substituted approximately 2 weeks before the planned initiation of antiretroviral therapy to allow time for rifampin''s enzyme induction effects to wane. In general, treatment of TB in the context of antiretroviral therapy is complex and requires an individualized approach. Experts in the treatment of HIV-related tuberculosis should be consulted, and TB and HIV care providers should work in close coordination throughout treatment.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative antimycobacterial agents should be considered in patients already receiving effective PI-containing antiretroviral therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79956/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, More', 'Paritaprevir, Zanamivir, Velpatasvir, Elbasvir, Famciclovir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, Telbivudine, Entecavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79856, 'Darunavir', 'Rifapentine', 'Major', 'Coadministration with rifapentine may decrease the plasma concentrations and antiretroviral effects of protease inhibitors. The mechanism is rifapentine induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of protease inhibitors.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of rifapentine should be avoided in patients receiving protease inhibitors.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79957/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, More', 'Lamivudine, Paritaprevir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79857, 'Darunavir', 'Rilpivirine', 'Moderate', 'Coadministration with darunavir/ritonavir may increase the plasma concentrations of rilpivirine. The proposed mechanism is darunavir/ritonavir inhibition of the CYP450 3A4-mediated metabolism of rilpivirine. The clinical significance of these changes is unknown. The pharmacokinetics of darunavir were not significantly altered.', 'DDInter', 'No dosage adjustment is required for either darunavir/ritonavir or rilpivirine during concomitant use, but monitoring for potentially increased adverse effects of rilpivirine such as depression, hepatotoxicity, and prolongation of the QT interval may be advisable.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79958/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, Velpatasvir, More', 'Lamivudine, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Stavudine, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79858, 'Darunavir', 'Rimegepant', 'Moderate', 'INTERVAL: Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of rimegepant, which is primarily metabolized by the isoenzyme. Coadministration with a weak CYP450 3A4 inhibitor is not expected to have a clinically significant effect on rimegepant exposures.', 'DDInter', 'Following concomitant administration with a moderate CYP450 3A4 inhibitor, the manufacturer recommends avoiding another dose of rimegepant within 48 hours.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79959/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Clonidine, Almotriptan, Fremanezumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79859, 'Darunavir', 'Ripretinib', 'Moderate', 'Coadministration of CYP450 3A4 inhibitors with ripretinib may increase the plasma concentrations and the risk of adverse effects (e.g., palmar-plantar erythrodysesthesia, hypertension, cardiac dysfunction, arthralgia/myalgia) of ripretinib and its active metabolite (DP-5439). The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', 'DDInter', 'Caution is recommended when ripretinib is used concomitantly with a CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects.', 'Metabolism', 'Caution is recommended when ripretinib is used concomitantly with a CYP450 3A4 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79960/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Sunitinib, Futibatinib, Regorafenib, Imatinib, Alpelisib, Lenvatinib, Erdafitinib, Trametinib, Nintedanib, Vandetanib, Ribociclib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79860, 'Darunavir', 'Rivaroxaban', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 3A4 may increase the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme.', 'DDInter', 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79961/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Betrixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79861, 'Darunavir', 'Ropivacaine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ropivacaine. Although ropivacaine is primarily metabolized by CYP450 1A2, it has been shown to undergo some metabolism via CYP450 3A4.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79962/', '', 'Chloroprocaine, Bupivacaine, Tetracaine, Mepivacaine, Meloxicam, Procaine, Prilocaine, Articaine, Etidocaine', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79862, 'Darunavir', 'Rosiglitazone', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79963/', '', 'Guar gum', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Etravirine, Dolutegravir, Oseltamivir, Cobicistat, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79863, 'Darunavir', 'Rosuvastatin', 'Major', 'Coadministration with protease inhibitors may significantly increase the plasma concentrations of rosuvastatin. The mechanism may involve inhibition of OATP1B1-mediated hepatic uptake and/or BCRP-mediated intestinal and hepatobiliary efflux. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. If coadministration is required, rosuvastatin should be initiated at the lowest effective dosage and not exceed 10 mg per day when prescribed with lopinavir-ritonavir, atazanavir-ritonavir, atazanavir-cobicistat, or ombitasvir/paritaprevir/ritonavir plus dasabuvir. The dosage of rosuvastatin should be limited to 20 mg once a day when used with darunavir-cobicistat. Alternatively, a different HMG-CoA reductase inhibitor such as fluvastatin may be considered. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Excretion', 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79964/', '', 'Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate, Gemfibrozil, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79864, 'Darunavir', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 3A4.', 'DDInter', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79965/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79865, 'Darunavir', 'Ruxolitinib', 'Minor', 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme. No dosage adjustment is recommended when ruxolitinib is coadministered with mild or moderate CYP450 3A4 inhibitors.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79966/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Sunitinib, Futibatinib, Regorafenib, Imatinib, Alpelisib, Lenvatinib, Erdafitinib, Trametinib, Nintedanib, Vandetanib, Ribociclib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79866, 'Darunavir', 'Salmeterol', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic levels and pharmacologic effects of salmeterol, which is primarily metabolized by the isoenzyme. Because salmeterol prolongs the QT interval in a dose-dependent manner, high systemic levels of salmeterol may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended. Some authorities recommend avoiding concomitant use of salmeterol during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79967/', '', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Olodaterol, Cromoglicic acid, Ipratropium, Salbutamol, Orciprenaline, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Etravirine, Dolutegravir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79867, 'Darunavir', 'Saquinavir', 'Moderate', 'Coadministration with saquinavir may decrease the plasma concentrations of darunavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster. The mechanism of interaction has not been described.', 'DDInter', 'The appropriate dosage of darunavir when prescribed in combination with saquinavir has not been established. Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of darunavir with saquinavir is not recommended, with or without low-dose ritonavir.', 'Others', 'The appropriate dosage of darunavir when prescribed in combination with saquinavir has not been established.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79968/', '', 'Lamivudine, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Stavudine, Famciclovir, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Remdesivir, Famciclovir, Maribavir, Pibrentasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79868, 'Darunavir', 'Secobarbital', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79969/', '', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Quazepam, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79869, 'Darunavir', 'Selpercatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme. Increased exposure to selpercatinib may increase the risk of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema, and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis).', 'DDInter', 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends decreasing the dosage of selpercatinib. Patients receiving selpercatinib 120 mg twice daily should have the dosage reduced to 80 mg twice daily, and those receiving 160 mg twice daily should have the dosage reduced to 120 mg twice daily. Close monitoring for adverse effects is advisable, including more frequent ECGs and laboratory monitoring of liver enzymes, bilirubin, electrolytes, glucose, and blood counts. Selpercatinib treatment should be discontinued, interrupted, or dosage reduced in patients with serious or life-threatening toxicities in accordance with the product labeling.', 'Metabolism', 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79970/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Sunitinib, Futibatinib, Regorafenib, Imatinib, Alpelisib, Lenvatinib, Er