INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79989, 'Dexlansoprazole', 'Dasatinib', 'Major', 'Nonclinical data indicate that the solubility of dasatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors is likely to reduce dasatinib systemic exposure.', 'DDInter', 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended. If gastric acidity management is necessary, antacids should be considered in place of these agents. However, patients treated with dasatinib should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and two hours after administration of dasatinib.', 'Absorption', 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80090/', '', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, More', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79990, 'Diazepam', 'Dasatinib', 'Moderate', 'Coadministration with dasatinib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dasatinib, which is a time-dependent inhibitor of the isoenzyme.', 'DDInter', 'Caution is advised if dasatinib must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dasatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if dasatinib must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80091/', '', 'Clobazam, Meprobamate, Halazepam, Chlordiazepoxide, Oxazepam', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, More', 1767369485);
