INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80019, 'Eletriptan', 'Dasatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should have vital signs monitored regularly and advised to notify their physician if they experience signs and symptoms of vasospasm such as numbness, tingling, or cyanosis in the extremities; muscle pains; weakness; or chest pain or tightness. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80120/', '', 'Frovatriptan, Eptinezumab, Rimegepant, Lasmiditan, Rizatriptan, Ubrogepant, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Clonidine, More', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80020, 'Eliglustat', 'Dasatinib', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80121/', '', 'Acalabrutinib, Tepotinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Axitinib, Sunitinib, More', 'Lipoic acid, Triheptanoin, Teduglutide, Fosdenopterin, Triethylenetetramine, Miglustat, Sapropterin, Zinc acetate, Levo