INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80122, 'Dasatinib', 'Lansoprazole', 'Major', 'Nonclinical data indicate that the solubility of dasatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors is likely to reduce dasatinib systemic exposure.', 'DDInter', 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended. If gastric acidity management is necessary, antacids should be considered in place of these agents. However, patients treated with dasatinib should avoid taking antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) for at least two hours before and two hours after administration of dasatinib.', 'Absorption', 'Concomitant use of dasatinib with H2-receptor antagonists or proton pump inhibitors is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80223/', '', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Larotrectinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, More', 1767369485);
