INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80306, 'Dasatinib', 'Rufinamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of dasatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The potential for diminished pharmacologic effects of dasatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected, or a dosage increase for dasatinib depending on patient tolerability. Close monitoring for toxicities (e.g., myelosuppression, bleeding complications, fluid retention, QT prolongation) is recommended if the dosage of dasatinib is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'The potential for diminished pharmacologic effects of dasatinib should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80407/', '', 'Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Fedratinib, Vandetanib, Afatinib, Tivozanib, Lorlatinib, Regorafenib, More', 'Ethotoin, Brivaracetam, Gabapentin, Cannabidiol, Topiramate, Paramethadione, Mephenytoin, Perampanel, Methsuximide, Phensuximide, Lamotrigine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80307, 'Dasatinib', 'Ruxolitinib', 'Major', 'Coadministration of dasatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with dasatinib is associated with severe thrombocytopenia.', 'DDInter', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for the first 2 months, then monthly thereafter or as clinically indicated. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80408/', '', 'Dacomitinib, Tepotinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Brigatinib, Encorafenib, More', 'Futibatinib, Alpelisib, Trametinib, Erdafitinib, Neratinib, Nintedanib, Dasatinib, Capmatinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
