INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80655, 'Daunorubicin', 'Selpercatinib', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80756/', '', 'Bleomycin, Valrubicin, Plicamycin, Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, More', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Ibrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80656, 'Daunorubicin', 'Sertraline', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80757/', '', 'Ixabepilone, Dactinomycin, Mitomycin, Valrubicin, Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, More', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Paroxetine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, More', 1767369485);
