INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80690, 'Daunorubicin', 'Toremifene', 'Major', 'Toremifene has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided. If treatment with other QT-prolonging drugs is required, interruption of toremifene therapy should be considered. Caution and clinical monitoring are recommended if concomitant use is unavoidable. In patients at increased risk, electrocardiograms (ECGs) are recommended at baseline and as clinically indicated. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80791/', '', 'Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80691, 'Daunorubicin', 'Trastuzumab', 'Major', 'The use of trastuzumab and anthracyclines in combination has been associated with a high risk of cardiotoxicity. Trastuzumab and agents in the anthracycline class are individually cardiotoxic and may have additive effects during coadministration.', 'DDInter', 'Trastuzumab and cardiotoxic agents such as anthracyclines should not be used in combination except in settings where cardiac monitoring is possible. The use of anthracycline-based therapy should be avoided, if possible, for up to 7 months after stopping trastuzumab. If anthracyclines must be used sooner, the patient''s cardiac function should be closely monitored.', 'Synergism', 'Trastuzumab and cardiotoxic agents such as anthracyclines should not be used in combination except in settings where cardiac monitoring is possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80792/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Necitumumab, Dostarlimab, Amivantamab, Ramucirumab, Atezolizumab, Pembrolizumab, More', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80692, 'Daunorubicin', 'Trazodone', 'Moderate', 'Trazodone may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of trazodone with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Trazodone should also not be used in patients with risk factors for QT prolongation. Hypokalemia and hypomagnesemia should be corrected prior to initiation of trazodone treatment and periodically monitored. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. When trazodone is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', 'Coadministration of trazodone with other drugs that can prolong the QT interval should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80793/', '', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Paroxetine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, More', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, More', 1767369485);
