INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80784, 'Fostemsavir', 'Daunorubicin (liposomal)', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80885/', '', 'Valrubicin, Plicamycin, Ixabepilone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Etravirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (80785, 'Gadobenic acid', 'Daunorubicin (liposomal)', 'Moderate', 'Gadobenate dimeglumine may compete with other substrates of the canalicular multispecific organic anion transporter, also known as cMOAT or MRP2, which is an endogenous transporter protein that works in coordination with the conjugation process to facilitate drug excretion. In solid tumor cells, overexpression of MRP2 confers resistance to a wide variety of anticancer chemotherapeutic agents. Substrates of MRP2 include anthracyclines, protease inhibitors, taxanes, vinca alkaloids, cisplatin, etoposide, methotrexate, and tamoxifen. Theoretically, competition for MRP2 transport may interfere with the clearance of these drugs and/or gadobenate dimeglumine.', 'DDInter', 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2. Patients should be monitored for potentially increased pharmacologic effects of gadobenate dimeglumine as well as the coadministered drug(s).', 'Excretion', 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/80886/', '', 'Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin, Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, More', 'Gadoversetamide, Gadobutrol, Gadofosveset trisodium, Gadopentetic acid, Gadoxetic acid, Gadoteric acid, Gadodiamide, Gadoteridol, Ferumoxsil', 1767369485);
