INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (81306, 'Decitabine', 'Tofacitinib', 'Major', 'Coadministration of tofacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids. Lymphoma and other malignancies have also been observed with tofacitinib use, with or without concomitant immunosuppressants.', 'DDInter', 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and tofacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81407/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (81307, 'Decitabine', 'Topotecan', 'Moderate', 'The use of topotecan with other cytotoxic agents is likely to result in greater myelosuppression due to additive or synergistic pharmacologic effects. Severe neutropenia and neutropenic sepsis resulting in death were reported with topotecan and cisplatin.', 'DDInter', 'Caution is advised if topotecan is used with other cytotoxic agents. Dosage reductions may be required, and bone marrow function should be closely monitored. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', 'Caution is advised if topotecan is used with other cytotoxic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81408/', '', 'Trabectedin, Cabazitaxel, Vincristine, Paclitaxel, Teniposide, Irinotecan, Docetaxel', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (81308, 'Decitabine', 'Trichophyton mentagrophytes', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/81409/', '', '', '', 1767369485);
