INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (81901, 'Edoxaban', 'Defibrotide', 'Major', 'Coadministration of defibrotide with anticoagulants may increase the risk of bleeding. In vitro, defibrotide increased the activity of fibrinolytic enzymes; therefore, it may enhance the pharmacologic effects of antithrombotic agents.', 'DDInter', 'Concomitant use of defibrotide with systemic anticoagulants (except for routine maintenance or reopening of central venous lines) is considered contraindicated. Anticoagulants should be discontinued prior to initiation of defibrotide. Preferably, defibrotide administration should be delayed until the effects of anticoagulant therapy have subsided. Some authorities recommend avoiding use of anticoagulants within 24 hours of defibrotide administration, or within 12 hours in the case of unfractionated heparin. Patients should be monitored for signs of bleeding during defibrotide therapy. If bleeding develops, defibrotide should be discontinued and treatment/supportive care provided until the bleeding stops.', 'Synergism', 'Concomitant use of defibrotide with systemic anticoagulants (except for routine maintenance or reopening of central venous lines) is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82002/', '', 'Acenocoumarol, Selexipag, Edoxaban', 'Defibrotide, Protein C, Acenocoumarol, Selexipag', 1767369485);
