INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82135, 'Deflazacort', 'Mifepristone', 'Major', 'Mifepristone is a potent antiglucocorticoid and may antagonize the effects of corticosteroids. Pharmacokinetically, mifepristone may increase the plasma concentrations of corticosteroids by inhibiting their metabolism via CYP450 3A4. However, the clinical impact has not been studied. Dexamethasone, specifically, may also decrease the blood levels of mifepristone by inducing its metabolism via CYP450 3A4.', 'DDInter', 'The use of mifepristone is considered contraindicated in patients on long-term corticosteroid therapy. When intended for daily use to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone is also contraindicated in those patients who require systemic corticosteroids for serious medical conditions or illnesses, such as immunosuppression after organ transplantation. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism, Antagonism', 'The use of mifepristone is considered contraindicated in patients on long-term corticosteroid therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82236/', '', 'Ulipristal, Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82136, 'Deflazacort', 'Miglitol', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82237/', '', 'Simvastatin, Guar gum, Rosuvastatin', '', 1767369485);
