INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82484, 'Didanosine', 'Delafloxacin', 'Moderate', 'INTERVAL: Coadministration with didanosine buffered tablets or pediatric oral solution may significantly reduce the oral bioavailability of ciprofloxacin and other quinolone antibiotics. The mechanism is reduced quinolone absorption due to chelation with metallic cations from buffering agents and antacids used in certain formulations of didanosine.', 'DDInter', 'Quinolones such as ciprofloxacin should be administered at least 2 hours before or 6 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy. Didanosine buffered powder for oral solution, which uses a citrate-phosphate buffer, and the delayed-release capsules, which are not buffered, are not expected to cause this interaction.', 'Absorption', 'Quinolones such as ciprofloxacin should be administered at least 2 hours before or 6 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82585/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82485, 'Diflunisal', 'Delafloxacin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', 'DDInter', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82586/', '', 'Acetaminophen, Ziconotide, Methoxyflurane', '', 1767369485);
