INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82547, 'Delafloxacin', 'Lasmiditan', 'Moderate', 'Coadministration with lasmiditan may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters. The proposed mechanism, based on in vitro data, involves decreased clearance due to inhibition of P-gp and BCRP by lasmiditan.', 'DDInter', 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible. If coadministration is required, patients should be carefully monitored for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Distribution', 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82648/', '', 'Frovatriptan, Dihydroergotamine, Rimegepant, Eptinezumab, Atogepant, Eletriptan, Rizatriptan, Ubrogepant, Ergotamine, Clonidine, Naratriptan, More', 'Enoxacin, Gatifloxacin, Norfloxacin, Nalidixic acid, Gemifloxacin, Ciprofloxacin, Lomefloxacin, Grepafloxacin, Moxifloxacin, Cinoxacin, Trovafloxacin, More', 1767369485);
