INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82589, 'Delafloxacin', 'Probenecid', 'Minor', 'Probenecid has been shown to interfere with the urinary excretion of certain quinolone antibiotics, resulting in increased plasma quinolone concentrations in some cases. The clinical relevance of this interaction is unknown but may be greater for quinolones that undergo significant tubular secretion via the renal anion transporter system such as cinoxacin, ciprofloxacin, enoxacin, gemifloxacin, nalidixic acid, and norfloxacin.', 'DDInter', '-', 'Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82690/', '', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Sulfinpyrazone, Rasburicase, Allopurinol, Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82590, 'Delafloxacin', 'Procarbazine', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82691/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Tagraxofusp, Pegaspargase, Ixazomib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82591, 'Delafloxacin', 'Repaglinide', 'Major', 'Quinolone antibiotics may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion. Both hyperglycemia and hypoglycemia have been reported, usually in diabetic patients receiving concomitant treatment with an oral hypoglycemic agent or insulin. Although hyperglycemia is significantly more common and infection itself may be an underlying risk factor, hypoglycemia may cause greater morbidity and mortality.', 'DDInter', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill. Due to the risk of profound and potentially life-threatening hypoglycemia, particular caution is advised during concomitant use of insulin and insulin secretagogues (e.g., sulfonylureas, meglitinides). Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the quinolone, and contact their physician. Alternative antibiotics may need to be considered.', 'Antagonism', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82692/', '', 'Simvastatin, Guar gum, Rosuvastatin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82592, 'Delafloxacin', 'Ribociclib', 'Moderate', 'Coadministration with ribociclib may increase the plasma concentrations and pharmacologic effects of drugs that are substrates of breast cancer resistance protein (BCRP), organic cation transporter-2 (OCT-2), multidrug and toxin extrusion 1 (MATE1), and/or bile salt efflux pump (BSEP) transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to ribociclib-mediated inhibition of these transport proteins at clinically relevant concentrations.', 'DDInter', 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ribociclib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82693/', '', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, More', 'Enoxacin, Trovafloxacin, Nalidixic acid, Cinoxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82593, 'Delafloxacin', 'Rofecoxib', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', 'DDInter', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82694/', '', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', '', 1767369485);
