INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82691, 'Everolimus', 'Delavirdine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia may be increased.', 'DDInter', 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided. Some authorities recommend avoiding concomitant use of everolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82792/', '', 'Lamivudine, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib, Golimumab, Ustekinumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82692, 'Fedratinib', 'Delavirdine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of fedratinib, which is a substrate of the isoenzyme. The risk of adverse reactions such as anemia, thrombocytopenia, diarrhea, nausea, vomiting, and liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations may increase.', 'DDInter', 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided. However, if coadministration with a potent CYP450 3A4 inhibitor is necessary, the manufacturer recommends that the dose of fedratinib be reduced to 200 mg once daily. Patients should be advised to notify their health care professional if signs and symptoms of anemia, thrombocytopenia, or gastrointestinal, hepatic, or pancreatic toxicity develop. Further dosage adjustment may be required based on patient tolerance and response. When/if concomitant therapy with a potent CYP450 3A4 inhibitor is ceased, the dose of fedratinib should first be increased to 300 mg once daily for two weeks an then increased to 400 mg once daily thereafter as tolerated.', 'Metabolism', 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82793/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
