INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82765, 'Delavirdine', 'Midazolam', 'Major', 'The coadministration with delavirdine may significantly increase the plasma concentrations and pharmacologic effects of alprazolam, midazolam, and triazolam. The mechanism is delavirdine inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme that metabolizes these benzodiazepines.', 'DDInter', 'Given the potential for serious and/or life-threatening reactions such as prolonged or increased sedation and respiratory depression, concomitant use of midazolam or triazolam with delavirdine is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82866/', '', 'Flurazepam, Methohexital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, Paraldehyde, Ethchlorvynol, Zopiclone, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82766, 'Delavirdine', 'Midostaurin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme. The increase in midostaurin concentrations may be particularly pronounced when CYP450 3A4 inhibitors are administered during the first week of midostaurin administration.', 'DDInter', 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82867/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, More', 'Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82767, 'Delavirdine', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. The clinical impact of this interaction has not been studied.', 'DDInter', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, the dosage of mifepristone should be limited to 300 mg/day and used only when necessary.', 'Metabolism', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, extreme caution is advised when mifepristone is prescribed in combination with strong CYP450 3A4 inhibitors such as, but not limited to, itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, delavirdine, nefazodone, telithromycin, and protease inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82868/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 'Raloxifene, Prasterone, Danazol, Bazedoxifene', 1767369485);
