INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82772, 'Delavirdine', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of delavirdine due to induction of CYP450 3A4 metabolism. Conversely, delavirdine may increase the plasma concentrations of modafinil due to inhibition of CYP450 3A4. The clinical significance is unknown. Modafinil and armodafinil are modest CYP450 3A4 inducers in vitro, while delavirdine is considered a potent inhibitor of the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with delavirdine. Clinical monitoring for altered effects of both drugs may be appropriate following addition or withdrawal of one or the other drug.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with delavirdine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82873/', '', 'Lamivudine, Paritaprevir, Cabotegravir, Simeprevir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Pemoline, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Citicoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82773, 'Delavirdine', 'Montelukast', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 2C9 may increase the plasma concentrations of montelukast, which is metabolized by these isoenzymes in addition to CYP450 3A4. When montelukast was administered with the potent CYP450 2C8/2C9 inhibitor gemfibrozil, montelukast systemic exposure (AUC) increased by 4.4-fold.', 'DDInter', 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil. However, it may be advisable to monitor patients for potentially increased adverse effects during coadministration with gemfibrozil or other CYP450 2C8/2C9 inhibitors.', 'Metabolism', 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82874/', '', 'Theophylline, Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Cabotegravir, Telaprevir, Velpatasvir, Rilpivirine, Dolutegravir, More', 1767369485);
