INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82848, 'Delavirdine', 'Ruxolitinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Indication specific dose modifications should be made when ruxolitinib is coadministered with strong CYP450 3A4 inhibitors. The recommended starting dose for patients with Polycythemia vera (PV) coadministered potent CYP450 3A4 inhibitors is ruxolitinib 5 mg twice a day. For patients with MF or PV who are stabilized on ruxolitinib 10 mg twice a day or greater and starting a potent CYP450 3A4 inhibitor, the ruxolitinib dose should be reduced by 50% (rounded up to the closest available tablet strength). For patients with MF or PV stabilized on a dose of 5 mg twice a day and starting fluconazole (at a dose of 200 mg per day or less), the ruxolitinib dose should be reduced to 5 mg once a day. For patients with MF or PV stabilized on ruxolitinib 5 mg once a day, concomitant use of strong CYP450 3A4 inhibitors should be avoided or ruxolitinib therapy interrupted for the duration of strong CYP450 3A4 inhibitor use. For patients with for acute graft versus host disease (GVHD) coadministered strong CYP450 3A4 inhibitors, the ruxolitinib dose should be reduced to 5 mg once a day with concomitant ketoconazole use; however, no dose adjustments are necessary with other potent CYP450 3A4 inhibitors.', 'Metabolism', 'Indication specific dose modifications should be made when ruxolitinib is coadministered with strong CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82949/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82849, 'Delavirdine', 'Salmeterol', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic levels and pharmacologic effects of salmeterol, which is primarily metabolized by the isoenzyme. Because salmeterol prolongs the QT interval in a dose-dependent manner, high systemic levels of salmeterol may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended. Some authorities recommend avoiding concomitant use of salmeterol during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82950/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Etravirine, Dolutegravir, More', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Olodaterol, Cromoglicic acid, Ipratropium, Salbutamol, Orciprenaline, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82850, 'Delavirdine', 'Saquinavir', 'Moderate', 'Coadministration with delavirdine may significantly increase the plasma concentrations of saquinavir. The mechanism is delavirdine inhibition of saquinavir metabolism via CYP450 3A4.', 'DDInter', 'Given the magnitude of interaction, a reduction of saquinavir SGC dosage should be considered during coadministration with delavirdine. Product labeling for the individual agents states that appropriate dosages for this combination with respect to safety and efficacy have not been established, although some HIV experts recommend that saquinavir SGC be given at 800 mg three times a day with the standard dosage of delavirdine. No dosage recommendations are available for saquinavir HGC or for saquinavir (either SGC or HGC) in combination with ritonavir. Patients receiving saquinavir with delavirdine should be monitored for toxicity such as elevations in liver function tests and undue gastrointestinal disturbances.', 'Metabolism', 'Given the magnitude of interaction, a reduction of saquinavir SGC dosage should be considered during coadministration with delavirdine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82951/', '', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Remdesivir, Famciclovir, Maribavir, Pibrentasvir, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Acyclovir, More', 1767369485);
