INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82873, 'Delavirdine', 'Tacrolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the blood concentrations of tacrolimus, which is primarily metabolized by the isoenzyme in both the intestine and liver. There have been numerous reports and pharmacokinetic studies in the medical literature of tacrolimus interaction with various potent CYP450 3A4 inhibitors, including azole antifungal agents, macrolide antibiotics, and protease inhibitors. Nephrotoxicity and other adverse effects (e.g., hyperkalemia, hyperglycemia, hemolytic anemia, hemolytic uremic syndrome, neurotoxicity) in association with significantly elevated tacrolimus blood levels have been reported, necessitating substantial reductions or interruptions in tacrolimus dosing.', 'DDInter', 'Caution is advised when tacrolimus is used with potent CYP450 3A4 inhibitors. Dosage reduction and/or prolongation of the dosing interval for tacrolimus will likely be required. Frequent monitoring of tacrolimus whole blood levels should be performed during and after discontinuation of the CYP450 3A4 inhibitor, and the tacrolimus dosage adjusted accordingly. In addition, patients should be closely monitored for development of serious adverse effects such as nephrotoxicity, lymphoma and other malignancies, infections, diabetes, neurotoxicity (tremor, paraesthesia, encephalopathy, delirium, coma), hyperkalemia, QT prolongation, myocardial hypertrophy, and hypertension. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised when tacrolimus is used with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82974/', '', 'Cabotegravir, Maraviroc, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Brincidofovir, Elbasvir, Voxilaprevir, Pibrentasvir, Rimantadine, More', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Caffeine, Cromoglicic acid, Dupilumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (82874, 'Delavirdine', 'Tadalafil', 'Moderate', 'Coadministration with drugs that are potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension. In addition, some authorities recommend avoiding concomitant use of tadalafil during and for 2 weeks after treatment with itraconazole in patients with pulmonary arterial hypertension. For erectile dysfunction, tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours when taken on an as needed basis in patients treated concomitantly with a potent CYP450 3A4 inhibitor. When taken on a daily basis, the dose should not exceed 2.5 mg per day. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/82975/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 1767369485);
