INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (84358, 'Deutetrabenazine', 'Ozanimod', 'Major', 'Tetrabenazine may cause excitation and hypertension in patients treated with monoamine oxidase inhibitors (MAOIs). The proposed mechanism has not been fully established but may be related to interference with biogenic amine storage. In addition, tetrabenazine may have depressant effects and may exacerbate depression.', 'DDInter', 'Concurrent administration of tetrabenazine and deutetrabenazine with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine) is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with either tetrabenazine or deutetrabenazine, and vice versa.', 'Synergism', 'Concurrent administration of tetrabenazine and deutetrabenazine with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84459/', '', 'Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Inotersen, Amifampridine', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (84359, 'Deutetrabenazine', 'Paliperidone', 'Major', 'Tetrabenazine causes central dopamine depletion by binding reversibly to human vesicular monoamine transporter type 2 (VMAT2) and interfering with presynaptic monoamine storage mechanisms. Coadministration of tetrabenazine in combination with neuroleptic agents or other dopamine antagonists (e.g., metoclopramide) may result in severe manifestations of dopamine deficiency. Deutetrabenazine, as well as many neuroleptic agents (e.g., asenapine, clozapine, droperidol, haloperidol, iloperidone, paliperidone, pimozide, phenothiazines, quetiapine, risperidone, sertindole, ziprasidone) and other dopamine antagonists (e.g., domperidone) have been associated with dose-related prolongation of the QT interval. Theoretically, the use of these agents in combination may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', 'DDInter', 'The use of deutetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferable be avoided. When coadministration is required, patients should be instructed to notify their physician if they experience extrapyramidal symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Clinicians, caregivers, and family members should be apprised of the risk of neuroleptic malignant syndrome and be alert to potential signs and symptoms such as mental status changes (e.g. mutism, catatonia, stupor, coma, agitation, confusion, hallucinations, delusions), autonomic instability, restlessness, rigidity, ataxia, myoclonus, hyperreflexia, tremors, diaphoresis, elevated creatine phosphokinase levels, and hyperpyrexia. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'The use of deutetrabenazine in combination with neuroleptic agents or other dopamine antagonists should preferable be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/84460/', '', 'Dalfampridine, Inotersen, Riluzole, Tafamidis', '', 1767369485);
