INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (84964, 'Amoxapine', 'Dexmethylphenidate', 'Moderate', 'The coadministration with methylphenidate may increase the serum concentrations of tricyclic antidepressants (TCAs). Case reports involving primarily methylphenidate and imipramine have suggested favorable as well as unfavorable clinical effects from this combination. In vitro studies suggest that methylphenidate may inhibit the metabolism of imipramine and other TCAs, although the extent is probably subject to considerable interindividual variation.', 'DDInter', 'Pharmacologic response to TCAs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85065/', '', 'Duloxetine, Desvenlafaxine, Maprotiline, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Milnacipran, Trazodone, Tryptophan, More', 'Pemoline, Metamfetamine, Solriamfetol, Caffeine, Citicoline', 1767369485);
