INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85095, 'Ribociclib', 'Promethazine', 'Major', 'Ribociclib can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of ribociclib with other drugs that can prolong the QT interval should generally be avoided. However, if concomitant use is required, caution and clinical and laboratory monitoring are recommended. ECGs should be assessed prior to initiation of treatment, during treatment when clinically necessary, and more frequently if QTcF prolongation occurs at any time during treatment. Ribociclib should be permanently discontinued if the QTcF interval prolongation is either greater than 500 msec or there is a greater than 60 msec change from baseline and associated with any of the following: Torsades de Pointes, polymorphic ventricular tachycardia, unexplained syncope, or signs/symptoms of serious arrhythmia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of ribociclib with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85196/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85096, 'Saquinavir', 'Promethazine', 'Major', 'Saquinavir in combination with ritonavir may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of ritonavir-boosted saquinavir with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85197/', '', 'Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Fexofenadine, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85097, 'Selpercatinib', 'Promethazine', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85198/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85098, 'Siponimod', 'Promethazine', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of siponimod treatment in patients receiving drugs that prolong the QT interval.', 'DDInter', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with siponimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85199/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85099, 'Sodium oxybate', 'Promethazine', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', 'DDInter', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.', 'Synergism', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85200/', '', 'Ketotifen, Levocetirizine, Cetirizine, Fexofenadine, Loratadine, Astemizole, Desloratadine, Azelastine, Terfenadine, Pheniramine, Lidocaine, More', 'Isoflurane, Nitrous oxide, Desflurane, Enflurane, Etomidate, Dalfampridine, Inotersen, Riluzole, Tafamidis', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85100, 'Sotalol', 'Promethazine', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85201/', '', 'Ketotifen, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, Meclizine, More', '', 1767369485);
